 var $SQL = array();
 function getSQL($id)
 {
  $this->SQL['StoreFeedback'] = "INSERT INTO FEEDBACK (NAME,EMAIL,TYPE,WHERE_FROM,LIVE_NOW,COMMENT,TIMESTAMP) VALUES (?,?,?,?,?,?,?)";
