  $this->SQL['GetMktPlcItemsDetails'] = "SELECT ITEM_ID,ITEM_CATEGORY,ITEM_TYPE,ITEM_TITLE,ITEM_DESC,ITEM_LOCATION,ITEM_IMG,POSTED_BY,POSTED_DATE,ITEM_COST,LAST_MOD_DATE,USER.USER_ID,USER.FIRSTNAME AS FIRSTNAME,USER.LASTNAME AS LASTNAME,USER.EMAIL_ADDRESS AS EMAIL_ADDRESS FROM MKTPLC_ITEM LEFT JOIN USER ON MKTPLC_ITEM.POSTED_BY=USER.USER_ID WHERE ITEM_ID=?";
  $this->SQL['GetMktPlcItemsByType'] = "SELECT ITEM_ID,ITEM_TYPE,ITEM_CATEGORY,ITEM_TITLE,ITEM_DESC,ITEM_LOCATION,ITEM_IMG,POSTED_BY,POSTED_DATE,ITEM_COST,LAST_MOD_DATE,USER.USER_ID,USER.FIRSTNAME AS FIRSTNAME,USER.LASTNAME AS LASTNAME,USER.EMAIL_ADDRESS AS EMAIL_ADDRESS FROM MKTPLC_ITEM LEFT JOIN USER ON MKTPLC_ITEM.POSTED_BY=USER.USER_ID WHERE ITEM_CATEGORY=? ORDER BY ? ? LIMIT ?, ?";
  $this->SQL['GetMktPlcItemsCount'] = "SELECT COUNT(*) AS COUNT FROM MKTPLC_ITEM";
  $this->SQL['GetMktPlcItemsCountByType'] = "SELECT COUNT(*) AS COUNT FROM MKTPLC_ITEM WHERE ITEM_CATEGORY=?";

