  $this->SQL['SaveDiscussionComments'] = "INSERT INTO LUM_COMMENT (DISCUSSIONID,AUTHUSERID,BODY,URL,DATECREATED,DELETED,DELETEUSERID) VALUES (?,?,?,?,?,?,?)";
  $this->SQL['GetDiscussionItemDetails'] = "SELECT LUM_DISCUSSION.DISCUSSIONID, LUM_DISCUSSION.CATEGORYID, LUM_DISCUSSION.AUTHUSERID, LUM_DISCUSSION.FIRSTCOMMENTID, LUM_DISCUSSION.LASTUSERID, LUM_DISCUSSION.NAME, LUM_DISCUSSION.DATECREATED, LUM_DISCUSSION.DATELASTACTIVE, LUM_DISCUSSION.COUNTCOMMENTS, LUM_COMMENT.BODY, LUM_COMMENT.URL, LUM_USER.NAME AS AUTHUSERNAME FROM LUM_DISCUSSION JOIN LUM_COMMENT ON LUM_DISCUSSION.DISCUSSIONID = LUM_COMMENT.DISCUSSIONID JOIN LUM_USER ON LUM_DISCUSSION.LASTUSERID = LUM_USER.USERID WHERE LUM_DISCUSSION.DISCUSSIONID = ? AND LUM_COMMENT.URL <> ''";
  $this->SQL['GetDiscussionItems'] = "SELECT LUM_DISCUSSION.DISCUSSIONID, LUM_DISCUSSION.CATEGORYID, LUM_DISCUSSION.AUTHUSERID, LUM_DISCUSSION.FIRSTCOMMENTID, LUM_DISCUSSION.LASTUSERID, LUM_DISCUSSION.NAME, LUM_DISCUSSION.DATECREATED, LUM_DISCUSSION.DATELASTACTIVE, LUM_DISCUSSION.COUNTCOMMENTS, LUM_COMMENT.BODY, LUM_COMMENT.URL, LUM_USER.NAME AS AUTHUSERNAME FROM LUM_DISCUSSION JOIN LUM_COMMENT ON LUM_DISCUSSION.DISCUSSIONID = LUM_COMMENT.DISCUSSIONID JOIN LUM_USER ON LUM_DISCUSSION.LASTUSERID = LUM_USER.USERID WHERE LUM_DISCUSSION.CATEGORYID = ? AND LUM_COMMENT.URL <> '' ORDER BY LUM_DISCUSSION.DATECREATED DESC LIMIT ?, ?";
  $this->SQL['IsDiscussionItemExists'] = "SELECT LUM_DISCUSSION.DISCUSSIONID, LUM_DISCUSSION.CATEGORYID, LUM_DISCUSSION.NAME, LUM_COMMENT.URL FROM LUM_DISCUSSION JOIN LUM_COMMENT ON LUM_DISCUSSION.DISCUSSIONID = LUM_COMMENT.DISCUSSIONID WHERE LUM_DISCUSSION.CATEGORYID = ? AND LUM_DISCUSSION.NAME = ? AND LUM_COMMENT.URL = ?";
  $this->SQL['GetDiscussionComments'] = "SELECT LUM_DISCUSSION.DISCUSSIONID, LUM_DISCUSSION.CATEGORYID, LUM_DISCUSSION.AUTHUSERID, LUM_DISCUSSION.FIRSTCOMMENTID, LUM_DISCUSSION.LASTUSERID, LUM_DISCUSSION.NAME, LUM_DISCUSSION.COUNTCOMMENTS, LUM_COMMENT.DATECREATED, LUM_COMMENT.BODY, LUM_COMMENT.URL, LUM_USER.NAME AS AUTHUSERNAME FROM LUM_COMMENT JOIN LUM_USER ON LUM_DISCUSSION.LASTUSERID = LUM_USER.USERID JOIN LUM_DISCUSSION ON LUM_COMMENT.DISCUSSIONID = LUM_DISCUSSION.DISCUSSIONID WHERE LUM_COMMENT.DISCUSSIONID = ? ORDER BY LUM_COMMENT.DATECREATED ASC LIMIT ?, ?";
  $this->SQL['UpdateDiscussionItem'] = "UPDATE LUM_DISCUSSION SET LUM_DISCUSSION.LASTUSERID = ?, LUM_DISCUSSION.COUNTCOMMENTS = ?, LUM_DISCUSSION.DATELASTACTIVE = ? WHERE LUM_DISCUSSION.DISCUSSIONID = ?";

  return $this->SQL{$id};
 }
}
